import { StyleSheet } from 'react-native';
import { Picker } from '@react-native-picker/picker';
import React from 'react';
import { useAppDispatch, useAppSelector } from '../ducks/useful-hooks';
import { setLanguage } from '../ducks/user-slice';



export class ChangeLanguage extends React.Component {
    constructor(props) {
        super(props);
        this.onChange;// = props.onChange;
        this.language;// = props.language;

    }



    render() {
        const user = useAppSelector((state) => state.user);
        const dispatch = useAppDispatch();

        const styles = StyleSheet.create({
            picker: {
                marginVertical: 30,
                height: 10,
                width: 120,
                fontSize: 11,
                padding: 0,
                margin: 0,
                color: '#1b3544',
            },
        });

        return (
            <Picker
                selectedValue={this.language}
                onValueChange={this.onChange}
                mode="dropdown" // Android only
                style={styles.picker}>
                <Picker.Item label="English" value="en"/>
                <Picker.Item label="עברית" value="he"/>
            </Picker>
        );
    }
};
