import React from 'react';
import addons, { types } from '@storybook/addons';
import ControlsPanel from './ControlsPanel';
import { AddonPanel } from './Panel';
export const ADDON_ID = 'RNCONTROLS';
export const PARAM_KEY = 'controls';
export function register() {
    addons.register(ADDON_ID, (api) => {
        addons.addPanel(ADDON_ID, {
            type: types.PANEL,
            title: 'Controls',
            render: ({ active, key }) => (React.createElement(AddonPanel, { active: active, key: key },
                React.createElement(ControlsPanel, { api: api }))),
            paramKey: PARAM_KEY,
        });
    });
}
